const hamburger=document.querySelector(".hamburger"),navMenu=document.querySelector(".nav-menu");hamburger.addEventListener("click",()=>{hamburger.classList.toggle("active"),navMenu.classList.toggle("active")}),document.querySelectorAll(".nav-link").forEach(n=>n.addEventListener("click",()=>{hamburger.classList.remove("active"),navMenu.classList.remove("active")}));const navbar=document.getElementById("navigation"),offset=navbar.offsetTop,introContainer=document.querySelector(".intro");function stickyNav(){window.pageYOffset>=offset?(navbar.classList.add("sticky"),introContainer.style.paddingTop="60px"):navbar.classList.remove("sticky")}window.onscroll=function(){stickyNav()};const contactForm=document.querySelector(".contact-square"),contactButton=document.querySelector(".contact-button"),projectSquare=document.querySelector(".projects-square");contactButton.addEventListener("click",()=>{contactForm.classList.toggle("active"),projectSquare.classList.toggle("active")});